import React, { Component } from "react";
import EmployeeDataService from "../services/employee.service";
import '../css/main.css';
import logo from '../images/agiliad-logo.png'
export default class Register extends Component {
  constructor(props) {
    super(props);
    this.onChangeUserName = this.onChangeUserName.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);
    this.onChangeretypePassword = this.onChangeretypePassword.bind(this);

    this.saveUser = this.saveUser.bind(this);
    this.newEmployee = this.newEmployee.bind(this);

    this.state = {
      id: null,
      UserName: "",
      Password: "", 
      retypePassword: "",
      submitted: false
    };
  }

  onChangeUserName(e) {
    this.setState({
      UserName: e.target.value
    });
  }

  onChangePassword(e) {
    this.setState({
      Password: e.target.value
    });
  }

  onChangeretypePassword(e) {
    this.setState({
      retypePassword: e.target.value
    });
  }

  saveUser() {
    var data = {
      UserName: this.state.UserName,
      Password: this.state.Password,
      retypePassword : this.state.retypePassword
    };

    EmployeeDataService.create(data)
      .then(response => {
        this.setState({
          id: response.data.id,
          UserName: response.data.UserName,
          Password: response.data.Password,
          retypePassword : response.data.retypePassword,
          //published: response.data.published,

          submitted: true
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  newEmployee() {
    this.setState({
      id: null,
      UserName: "",
      Password: "",
      //published: false,

      submitted: false
    });
  }
  render() {
    return (      
        <div>
            <link
                rel="stylesheet"
                href="https://cdn.jsdelivr.net/npm/bootstrap@5.0.2/dist/css/bootstrap.min.css"
                integrity="sha384-EVSTQN3/azprG1Anm3QDgpJLIm9Nao0Yz1ztcQTwFspd3yD65VohhpuuCOmLASjC"
                crossorigin="anonymous"
            />
            {/* Main Content */}
            <div className="search" >
                {/* <form action= "/register"  method="POST"> */}
                    <input className="prop small" 
                    align="center" 
                    type="text" 
                    value={this.state.UserName}
                    onChange={this.onChangeUserName}
                    name="UserName" 
                    id="UserName" placeholder="Username"/>
                    <input className="prop small" 
                    type="password" 
                    value={this.state.Password} 
                    name="Password" 
                    id="Password" 
                    placeholder="password" 
                    onChange={this.onChangePassword}/>
                    <input className="prop small" 
                    type="password" 
                    value={this.state.retypePassword} 
                    name="retypePassword" 
                    id="retypePassword" 
                    placeholder="retypepassword" 
                    onChange={this.onChangeretypePassword}/>
                    <input className="btnsubmit" onClick={this.saveUser} type="submit" value="Submit" /><br />
                {/* </form> */}
            </div>
            
        </div>
        
    )
    // return (
    //   <div className="submit-form">
    //     {this.state.submitted ? (
    //       <div>
    //         <h4>You Registered successfully!</h4>
    //         <button className="btn btn-success" onClick={this.newEmployee}>
    //           Add
    //         </button>
    //       </div>
    //     ) : (
    //       <div>
    //         <div className="form-group">
    //           <label htmlFor="title">UserName</label>
    //           <input
    //             type="text"
    //             className="form-control"
    //             id="UserName"
    //             required
    //             value={this.state.UserName}
    //             onChange={this.onChangeUserName}
    //             name="UserName"
    //           />
    //         </div>

    //         <div className="form-group">
    //           <label htmlFor="description">Password</label>
    //           <input
    //             type="text"
    //             className="form-control"
    //             id="Password"
    //             required
    //             value={this.state.Password}
    //             onChange={this.onChangePassword}
    //             name="Password"
    //           />
    //         </div>
    //         <div className="form-group">
    //           <label htmlFor="description">RetypePassword</label>
    //           <input
    //             type="password"
    //             className="form-control"
    //             id="retypePassword"
    //             required
    //             value={this.state.retypePassword}
    //             onChange={this.onChangeretypePassword}
    //             name="retypePassword"
    //           />
    //         </div>

    //         <button onClick={this.saveUser} className="btn btn-success">
    //           Submit
    //         </button>
    //       </div>
    //     )}
    //   </div>
    // );
  }
}
