import React, { Component } from "react";
import EmployeeDataService from "../services/employee.service";
import { Link } from "react-router-dom";

export default class EmployeesList extends Component {
  constructor(props) {
    super(props);
    this.onChangeSearchSkill = this.onChangeSearchSkill.bind(this);
    this.retrieveEmployees = this.retrieveEmployees.bind(this);
    this.refreshList = this.refreshList.bind(this);
    this.setActiveEmployee = this.setActiveEmployee.bind(this);
    //this.removeAllEmployees = this.removeAllEmployees.bind(this);
    this.searchSkill = this.searchSkill.bind(this);

    this.state = {
      Employees: [],
      currentEmployee: null,
      currentIndex: -1,
      searchSkill: ""
    };
  }

  componentDidMount() {
    this.retrieveEmployees();
  }

  onChangeSearchSkill(e) {
    const searchSkill = e.target.value;

    this.setState({
      searchSkill: searchSkill
    });
  }

  retrieveEmployees() {
    EmployeeDataService.getAll()
      .then(response => {
        this.setState({
          employees: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  refreshList() {
    this.retrieveEmployees();
    this.setState({
      currentEmployee: null,
      currentIndex: -1
    });
  }

  setActiveEmployee(employee, index) {
    this.setState({
      currentEmployee: employee,
      currentIndex: index
    });
  }

//   removeAllTutorials() {
//     TutorialDataService.deleteAll()
//       .then(response => {
//         console.log(response.data);
//         this.refreshList();
//       })
//       .catch(e => {
//         console.log(e);
//       });
//   }

  searchSkill() {
    EmployeeDataService.findBySkill(this.state.searchSkill)
      .then(response => {
        this.setState({
          employees: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }
  render() {
    const { searchSkill, employees, currentEmployee, currentIndex } = this.state;

    return (
      <div className="list row">
        <div className="col-md-8">
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Search by Skill"
              value={searchSkill}
              onChange={this.onChangeSearchSkill}
            />
            <div className="input-group-append">
              <button
                className="btn btn-outline-secondary"
                type="button"
                onClick={this.searchSkill}
              >
                Search
              </button>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <h4>Employees List</h4>

          <ul className="list-group">
            {employees &&
              employees.map((employee, index) => (
                <li
                  className={
                    "list-group-item " +
                    (index === currentIndex ? "active" : "")
                  }
                  onClick={() => this.setActiveEmployee(employee, index)}
                  key={index}
                >
                  {employee.UserName}
                </li>
              ))}
          </ul>

          {/* <button
            className="m-3 btn btn-sm btn-danger"
            onClick={this.removeAll}
          >
            Remove All
          </button> */}
        </div>
        <div className="col-md-6">
          {currentEmployee ? (
            <div>
              <h4>Employee</h4>
              <div>
                <label>
                  <strong>Title:</strong>
                </label>{" "}
                {currentEmployee.UserName}
              </div>
              <div>
                <label>
                  <strong>Skills:</strong>
                </label>{" "}
                {currentEmployee.Skillsets}
              </div>
              {/* <div>
                <label>
                  <strong>Status:</strong>
                </label>{" "}
                {currentTutorial.published ? "Published" : "Pending"}
              </div> */}

              <Link
                to={"/employees/" + currentEmployee.id}
                className="badge badge-warning"
              >
                Update
              </Link>
            </div>
          ) : (
            <div>
              <br />
              <p>Please click on a Employee...</p>
            </div>
          )}
        </div>
      </div>
    );
  }
}
